  var days = Math.floor(diffInSeconds / 86400);
  var hours = Math.floor((diffInSeconds % 86400) / 3600);
  var minutes = Math.floor((diffInSeconds % 3600) / 60);
  var seconds = Math.round(diffInSeconds % 60);

  // æ ¼å¼åŒ–å°æ—¶ã€åˆ†é’Ÿå’Œç§’
